let Data = {
  labels: ["META TGO", "META TCO", "META ET", "META OTROS"],
  datasets: [
    {
      data: [0, 0, 0, 0],
      backgroundColor: [
        "rgb(57, 169, 0,1)",
        "rgb(0, 50, 77,1)",
        "rgb(203, 119, 102,1)",
        "rgb(106, 106, 106,1)",
      ],
      hoverBackgroundColor: [
        "rgb(57, 169, 0,.75)",
        "rgb(0, 50, 77,.75)",
        "rgb(203, 119, 102,.75)",
        "rgb(106, 106, 106,.75)",
      ],
    },
  ],
}

function loadChartsOnHome(){
  One.onLoad(
    class {
      static initChartJS() {
        // Configuración de Chart.js
        Chart.defaults.color = "#818d96";
        Chart.defaults.font.weight = "600";
        Chart.defaults.scale.grid.color = "rgba(0, 0, 0, .05)";
        Chart.defaults.scale.grid.zeroLineColor = "rgba(0, 0, 0, .1)";
        Chart.defaults.scale.beginAtZero = true;
        Chart.defaults.elements.line.borderWidth = 2;
        Chart.defaults.elements.point.radius = 4;
        Chart.defaults.elements.point.hoverRadius = 6;
        Chart.defaults.plugins.tooltip.radius = 3;
        Chart.defaults.plugins.legend.labels.boxWidth = 15;
  
        // Creación del gráfico de doughnut
        const ctx = document.getElementById("js-chartjs-donut");
        const chart = new Chart(ctx, {
          type: "doughnut",
          data: Data,
          options: {
            title: {
              display: true,
              text: "",
            },
          },
        })
        // Creación del gráfico de bar
      const ctx2 = document.getElementById("js-chartjs-donut2");
      const chart2 = new Chart(ctx2, {
        type: "bar",
        data: Data,
        options: {
          title: {
            display: true,
            text: "",
          },
          indexAxis: "y"
        },
      });
      }

      static init() {
        this.initChartJS();
      }
    }.init()
  );
  
};


